import android.widget.Button;
import android.widget.EditText;
import android.widget.TextView;

public class MainActivity extends AppCompatActivity {
    EditText editText;
    Button btnSearch;
    TextView tvDetails;

    @Override
    protected void onCreate(Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);
        setContentView(R.layout.activity_main);

       editText=(EditText) findViewById(R.id.editTextTextPersonName);
       btnSearch=(Button) findViewById(R.id.button);
       tvDetails=(TextView) findViewById(R.id.textView);
    btnSearch.setOnClickListener(new View.OnClickListener() {
        @Override
        public void onClick(View view) {
           // String searchQuery = editText.getText().toString();
           // if (!searchQuery.isEmpty()) {
           //     tvDetails.setText(searchQuery);
          //  } else {
           //     tvDetails.setText("Por favor, ingresa un texto en la caja de texto.");
          //  }
           // String searchQuery = editText.getText().toString();
          //  if (!searchQuery.isEmpty()) {
          //      tvDetails.setText(searchQuery);
          //  } else if (searchQuery.length() > 50) {
          //      tvDetails.setText("El texto ingresado es demasiado largo. Por favor, ingresa un texto más corto.");
         //   }



           // else {
             //   tvDetails.setText("Por favor, ingresa un texto en la caja de texto.");
          //  }

            String searchQuery = editText.getText().toString();
            if (searchQuery.equals("placa base") || searchQuery.equals("corto circuito") || searchQuery.equals("no sube nivel de bateria")) {
                tvDetails.setText("R.string.error_placa");
                editText.setText("");
            }
            else if(searchQuery.isEmpty()) {
               tvDetails.setText("Por favor inserte la consulta que desea realizar");
                editText.setText("");
               // tvDetails.setText(searchQuery);
            }
            else if (searchQuery.equals("problemas de microfono") || searchQuery.equals("microfono") || searchQuery.equals("microfono") ||
            searchQuery.equals("error de microfono")|| searchQuery.equals("no me escuchan lo que hablo")|| searchQuery.equals("microfono dañado")) {
                tvDetails.setText(R.string.error_microfono);
                editText.setText("");
            }
            else if (searchQuery.equals("pantalla rota") || searchQuery.equals("no veo imagen") || searchQuery.equals("pantalla clisada") ||
                    searchQuery.equals("solo escucho sonido")|| searchQuery.equals("pantalla oscuro")) {
                tvDetails.setText(R.string.error_pantalla);
                editText.setText("");
            }
            else if (searchQuery.equals("no escucho sonido") || searchQuery.equals("no da audio") || searchQuery.equals("el parlante no suena") ||
                    searchQuery.equals("mi parlante esta dañado")|| searchQuery.equals("lo hice caer ahora al agua mi parlante no suena")) {
                tvDetails.setText(R.string.error_parlante);
                editText.setText("");
            }
            else if (searchQuery.equals("no carga") || searchQuery.equals("no puedo cargar mi celular") || searchQuery.equals("la entrada de carga esta dañado") ||
                    searchQuery.equals("se desoldo la entrada de carga")|| searchQuery.equals("la entrada de carda no funciona") || searchQuery.equals("modulo de carga dañado") ||
            searchQuery.equals("falla de modulo de carga") || searchQuery.equals("pin dañado")) {
                tvDetails.setText(R.string.error_carga);
                editText.setText("");
            }

            else if (searchQuery.equals("placa")) {
                tvDetails.setText(R.string.error_placa);
                editText.setText("");
            }

            else {
                tvDetails.setText("Escriba su consulta");
                editText.setText("");
            }



        }
    });

    }
}
